import "../Css-files/home.css"

import React, { useEffect, useState } from 'react';

export default function Home() {


    const [norrisJoke, setNorrisJoke] = useState("");
    const [dadJoke, setDadJoke] = useEffect("");
    const [getNorris, setGetNorris] = useEffect(false);

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users/")
            .then(r => r.Json)
            .then(d => setNorrisJoke(d.value))
    }, [getNorris]);




const baseUrl = "https://jsonplaceholder.typicode.com/users/" // Det endpoint som der bliver hentet fra
const resultBox = document.getElementById('result'); 


const mapMembersToHtml = (members) => {
  const html = members.map(({id, name, username, email, address, phone, company}) => (
    `<tr>
      <td>${id}</td>
      <td>${name}</td>
      <td>${username}</td>
      <td>${email}</td>
      <td>${address.street}</td>
      <td>${address.city}</td>
      <td>${address.zipcode}</td>
      <td>${phone}</td>
      <td>${company.name}</td>
    </tr>`
  )).join('');

  resultBox.innerHTML = 
          ` <table border= "2"> 
                <thead> 
                    <tr> 
                        <td>Person-ID</td> 
                        <td>Name</td> 
                        <td>Username</td> 
                        <td>Email</td> 
                        <td>Address</td> 
                        <td>City</td> 
                        <td>Zipcode</td> 
                        <td>Phone number</td>
                        <td>Hobby</td>
                    </tr> 
                </thead> 
                <tbody> 
                    ${html} 
                </tbody> 
              </table> `; 
}         

const searchInMembers = (query) => {
  if (query.length === 0) {
    return
  }
//Matcher basis værdi med den nye værdi, dette gør expansion langt nemmere.
  const matchValues = [
    (element) => element.name,
    (element) => element.phone,
    (element) => element.address.street,
    (element) => element.address.city,
    (element) => element.email
  ]

  const queryValue = query.toLowerCase()
  fetch(baseUrl)
    .then(response => response.json())
    .then(elements => {
      const filtered = elements.filter(element => {
        for (let matchValue of matchValues) {
          const comparableValue = matchValue(element).toLowerCase()
          if (queryValue === comparableValue || comparableValue.includes(queryValue)) {
            return true
          }
        }
        return false
      })
    mapMembersToHtml(filtered)
    })
} 




  return (
<div>  

  <h1 className="title"> Looking for a specific food or destination? Let <span className="logo">TravelEat </span> help! </h1>

  <div className="container"> 
      <div>    
        <input className="searchContainer" type="text" placeholder="Food or Destination"></input>
      </div>
  </div>
        <button className="searchButton" type="button" onClick={mapMembersToHtml}>Search</button>
        <div id="result" className="returnedResult" ></div>
</div> 
  );
}
