import React, { useEffect, useState } from 'react';


class MySearchSite extends React.Component {

    constructor(props) {
      super(props)

      this.state = {
        query: '',
        loadingData: true,
        data: []
      }

      this.loadInitialData()
    }

    async loadInitialData() {
      const response = await fetch('https://jsonplaceholder.typicode.com/users/')
      const json = await response.json()

      this.setState({ isLoading: false, data: json })
    }

    filterByQuery(query) {
      if (query.length === 0) {
        return []
      }

      const matches = [
        (element) => element.name,
        (element) => element.email
      ]

      const queryValue = query.toLowerCase()

      return this.state.data.filter(element => {
        for (let match of matches) {
          const comparableValue = match(element).toLowerCase()

          if (queryValue === comparableValue || comparableValue.includes(queryValue)) {
            return true
          }
        }

        return false
      })
    }

    render() {
      const { query, isLoading } = this.state

      if (isLoading) {
        return <p>Initializing</p>
      }

      const filteredData = this.filterByQuery(query)

      return (
        <div className='wrapper'>
          <h1>Looking for specific food?</h1>
          <div>
            <input value={ query } onChange={ (event) => this.setState({ query: event.target.value }) } type='text' placeholder='Food or destination'/>
            <div>
              <ul>
                { filteredData.map(entry => {
                  return (
                    <li key={ entry.name } onClick={ () => this.setState({ query: entry.name })}>
                        { entry.name }
                    </li>
                  )
                }) }
              </ul>
            </div>
          </div>
        </div>
      )
    }
  }

  ReactDOM.render(
    <MySearchSite/>,
    document.getElementById('app')
  );
