import {
  useHistory,

} from "react-router-dom";
import React, { useState, useEffect } from "react";

export default function LogIn({ login, init }) {
  const [loginCredentials, setLoginCredentials] = useState(init);

  const history = useHistory();

  const performLogin = (evt) => {
    evt.preventDefault();
    login(loginCredentials.username, loginCredentials.password);
    loginCredentials.username !== "admin"
      ? history.push("/account")
      : history.push("/statistics");
  };
  const onChange = (evt) => {
    setLoginCredentials({
      ...loginCredentials,
      [evt.target.id]: evt.target.value,
    });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onChange={onChange}>
      <div className="registerHeader"> Sign up with your email address</div>
      <div>
          <label className="nameField" >
            <input type="text" id="email" className="inputField" placeHolder="Email"onChange={onChange} />
          </label>
      </div>

      <div>
          <label className="nameField" >
            <input type="text" id="email" className="inputField" placeHolder="Confirm Email"onChange={onChange} />
          </label>
      </div>






        <input placeholder="Email" id="username" />
        <input placeholder="Password" id="password" />
        <button onClick={performLogin}>Login</button>
      </form>
    </div>
  );
}
