import { useEffect, useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import "../Css-files/Register.css"


export default function Register({ facade, init }) {
  const [newUser, setNewUser] = useState(init);
  const [status, setStatus] = useState("");
  const [nickName, setNickName] = useState("unknown");


  const [form, setForm] = useState(
    <form className="container"onSubmit={onSubmit}>
      <div>
          <label className="nameField" >
            Name:
            <input type="text" id="username" className="inputField" onChange={onChange} />
          </label>
      </div>

      <div>
          <label className="nameField">
          Password:
          <input type="password" id="password" className="inputField"  onChange={onChange} />
          </label>
      </div>

      <div>
          <label className="nameField">
          E-Mail:
          <input type="email" id="email" className="inputField"  onChange={onChange} />
          </label>
      </div>

      <div>
        <input className="makeButton" type="submit" value="Submit" />
      </div>

    </form>
  );

  function onSubmit(e) {
    e.preventDefault();
    console.log(newUser);
    console.log(nickName);
    facade
      .registerUser(newUser)
      .then((data) => {
        console.log(data);
        setStatus(data.msg);
        setNickName(data.username);
        setForm("");
      })
      .catch((err) => {
        if (err.status) {
          err.fullError.then((e) => setStatus(e.message));
        } else {
          setStatus("Network error has occurred: could not log in");
          console.log("Network error! Could not log in");
        }
      });
  }

  function onChange(e) {
    const target = e.target;
    // const value = target.type === "checkbox" ? target.checked : target.value;
    let value = target.value;
    let name = target.id;

    setNewUser({
      ...newUser,
      [name]: value,
    });
    console.log(newUser);
  }

  return (
    <div>
      <div>
        <h2 className="registerHeader">Sign Up To <span className="logo">TravelEat </span>  </h2>
      </div>
      <div>{form}</div>

      <div>
        <h1>{status}</h1>
      </div>
     </div>
  );
}

